create table members (member_dob date, no integer generated by default as identity, reference_member_no integer, address1 varchar(255), district varchar(255), email_id varchar(255) not null unique, member_name varchar(255) not null, mobile_number varchar(255), password varchar(255) not null, pincode varchar(255), place varchar(255), state varchar(255), taluk varchar(255), member_type enum ('PRIMARY','SECONDARY') not null, primary key (no), constraint uniq_member_composite unique (member_name, member_type, reference_member_no));




create table finance (current_balance float(53), finance_amount float(53), finance_creationdate date not null, finance_owner_no integer, id integer not null, finance_name varchar(255) not null, finance_type enum ('PRIMARY','SECONDARY') not null, primary key (id), constraint uniq_finance_composite unique (finance_type, finance_name, finance_owner_no));



create table revenue (approver_member_no integer, finance_id integer, revenue_number integer not null, spend_amount float(53) not null, spend_date date not null, spender_member_no integer, spender_details varchar(255), current_status enum ('APPPRVED','INPROGRESS','REJECTED') not null, primary key (revenue_number));